INSERT INTO mytable(id,name,description) VALUES (130,'jaggery','b''"Jaggery is a traditional non-centrifugal cane sugar consumed in the Indian Subcontinent and Southeast Asia. It is a concentrated product of cane juice and often date or palm sap without separation of the molasses and crystals, and can vary from golden brown to dark brown in colour."''');
