INSERT INTO mytable(id,name,description) VALUES (150,'linguine','b''"Linguine is a type of pasta similar to fettuccine and trenette but elliptical in section rather than flat. It is about 4 millimetres in width, which is wider than spaghetti but not as wide as fettuccine. The name linguine means "little tongues" in Italian, where it is a plural of the feminine linguina."''');
