INSERT INTO mytable(id,name,description) VALUES (192,'peanut-oil','b''"Peanut oil, also known as groundnut oil or arachis oil, is a vegetable oil derived from peanuts. The oil usually has a mild or neutral flavor but, if made with roasted peanuts, has a stronger peanut flavor and aroma."''');
INSERT INTO mytable(id,name,description) VALUES (193,'penne','b''"Penne is an extruded type of pasta with cylinder-shaped pieces, their ends cut at an angle. Penne is the plural form of the Italian penna, deriving from Latin penna, and is a cognate of the English word pen. When this format was created, it was intended to imitate the then-ubiquitous fountain pen\''s steel nibs."''');
