INSERT INTO mytable(id,name,description) VALUES (198,'potatoes','b''"The potato is a starchy tuber of the plant Solanum tuberosum and is a root vegetable native to the Americas. The plant is a perennial in the nightshade family Solanaceae. Wild potato species can be found throughout the Americas, from Canada to southern Chile."''');
