INSERT INTO mytable(id,name,description) VALUES (200,'ragi-flour','b''"Ragi Flour is a primarily a powder food, made out of Ragi grain. It is finger millet powder.It has high protein and mineral content. It is an ideal source of protein for vegetarians."''');
INSERT INTO mytable(id,name,description) VALUES (201,'rajma','b''"R\xc4\x81jm\xc4\x81 [ra\xcb\x90d\xca\x92ma\xcb\x90], also known as rajmah, r\xc4\x81zm\xc4\x81, or lal lobia, is a vegetarian dish, originating from the Indian subcontinent, consisting of red kidney beans in a thick gravy with many Indian whole spices, and is usually served with rice. It is a part of regular diet in India, Nepal, Bangladesh and Punjab province of Pakistan."''');
