INSERT INTO mytable(id,name,description) VALUES (203,'red-chili','b''"Chili peppers, from Nahuatl ch\xc4\xablli, are varieties of the berry-fruit of plants from the genus Capsicum, which are members of the nightshade family Solanaceae, cultivated for their pungency. Chili peppers are widely used in many cuisines as a spice to add "heat" to dishes."''');
