INSERT INTO mytable(id,name,description) VALUES (206,'relish','b''"A relish is a cooked and pickled product made of chopped vegetables, fruits, pickles or herbs and is a food item typically used as a condiment to enhance a staple. Examples are chutneys and the North American relish, a pickled cucumber jam eaten with hot dogs or hamburgers."''');
INSERT INTO mytable(id,name,description) VALUES (207,'rice-bran-oil','b''"Rice bran oil is the oil extracted from the hard outer brown layer of rice called chaff. It is known for its high smoke point of 232 \xc2\xb0C and mild flavor, making it suitable for high-temperature cooking methods such as stir frying and deep frying."''');
INSERT INTO mytable(id,name,description) VALUES (208,'rotini','b''"Rotini is a type of helix- or corkscrew-shaped pasta. The name comes from a 17th-century Italian word meaning "small wheels". Rotini is related to fusilli, but has a tighter helix, i.e. with a smaller pitch. It should not be confused with rotelle."''');
