INSERT INTO mytable(id,name,description) VALUES (230,'vermicelli','b''"Vermicelli is a traditional type of pasta round in section similar to spaghetti. In English-speaking regions it is usually thinner than spaghetti, while in Italy it is typically thicker. The term vermicelli is also used to describe various types of thin noodles from Asia. In Vietnam vermicelli is the same as angel hair pasta or capellini."''');
INSERT INTO mytable(id,name,description) VALUES (231,'vinegar','b''"Vinegar is an aqueous solution of acetic acid and trace compounds that may include flavorings. Vinegar typically contains 5\xe2\x80\x938% acetic acid by volume. Usually, the acetic acid is produced by a double fermentation; converting simple sugars to ethanol using yeast and ethanol to acetic acid by acetic acid bacteria."''');
INSERT INTO mytable(id,name,description) VALUES (232,'wheat-flour','b''"Wheat flour is a powder made from the grinding of wheat used for human consumption. Wheat varieties are called "soft" or "weak" if gluten content is low, and are called "hard" or "strong" if they have high gluten content."''');
